import React from "react";
import './AboutUs.css';
import { useNavigate } from "react-router-dom";
import NavBar from '../../components/NavBar/NavBar';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle } from '@fortawesome/free-solid-svg-icons';


const AboutUs = () => {
  return (

    <div className="about-us-container">
      <header>
        <NavBar/>
      </header>
      <main>
        <div className="about-us-div">
          <h1 className="about-us-div-title">Hakkımızda</h1>
          <div className="about-us-form">
            <h2 className="about-us-form-mission-title">Misyon</h2>
            <ul>
              <li><FontAwesomeIcon icon={faCircle} style={{color: 'black', fontSize: '12px', marginLeft:'32px', marginRight:'32px' }} />Eğitim ve rehberlik alanında mentorlarla öğrencileri bir araya getirerek, öğrencilere kişisel ve profesyonel gelişimlerinde destek sağlamak.</li>
              <li><FontAwesomeIcon icon={faCircle} style={{color: 'black', fontSize: '12px', marginLeft:'32px', marginRight:'32px' }} />Herkesin eşit fırsatlarla ilham alabileceği ve kendini geliştirebileceği bir mentorluk platformu sunmak.</li>
              <li><FontAwesomeIcon icon={faCircle} style={{color: 'black', fontSize: '12px', marginLeft:'32px', marginRight:'32px' }} />Öğrencilerin kariyer hedeflerine ulaşmasını kolaylaştırırken, mentorlara bilgi ve deneyimlerini paylaşma fırsatı vermek.</li>
            </ul>
            <h2 className="about-us-form-vision-title">Vizyon</h2>
            <ul>
              <li><FontAwesomeIcon icon={faCircle}  style={{color: 'black', fontSize: '12px', marginLeft:'32px', marginRight:'32px' }} />Küresel çapta lider bir mentorluk platformu haline gelerek, milyonlarca öğrenciye ve mentora ulaşmak.</li>
              <li><FontAwesomeIcon icon={faCircle} style={{color: 'black', fontSize: '12px', marginLeft:'32px', marginRight:'32px' }} />Eğitim ve kariyer alanında fırsat eşitliğini destekleyen, güvenilir ve yenilikçi bir platform oluşturmak.</li>
              <li><FontAwesomeIcon icon={faCircle} style={{color: 'black', fontSize: '12px', marginLeft:'32px', marginRight:'32px' }} />Teknoloji ve insan bağlantısını birleştirerek, mentorluk süreçlerini daha etkili ve erişilebilir kılmak.</li>
            </ul>
          </div>
        </div>
      </main>
    </div>


  );
};

export default AboutUs;
