import React, { useState } from "react";
import "./Login.css";
import { useNavigate } from 'react-router-dom';


const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate(); // useNavigate hook'u kullanƒ±lƒ±yor.


  const handleSignUpClick = () => {
    navigate('/signup'); // /login sayfasƒ±na y√∂nlendirir.
  };  

  const handleSubmit = async (e) => {
    e.preventDefault(); // Sayfanƒ±n yeniden y√ºklenmesini engeller

    setIsLoading(true); // Y√ºkleniyor durumu ba≈ülat
    setMessage(""); // √ñnceki mesajƒ± temizle

    try {
      const response = await fetch("http://localhost:5001/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email,
          password,
        }),
      });

      if (!response.ok) {
        // Hatalƒ± bir yanƒ±t durumunu kontrol et
        const errorData = await response.json();
        throw new Error(errorData.message || "Bir hata olu≈ütu.");
      }

      const data = await response.json();
      setMessage(data.message || "Giri≈ü ba≈üarƒ±lƒ±!");
      console.log("DATAAAAAA:",data);

    } catch (error) {
      setMessage(error.message || "Bir hata olu≈ütu. L√ºtfen tekrar deneyin.");
    } finally {
      setIsLoading(false); // Y√ºkleniyor durumunu sonlandƒ±r
    }
  };

  return (
    <div>
      <button className="register-button" onClick={handleSignUpClick}>Kayƒ±t ol</button>
      <div className="login-container">
        <div className="login-card">
          <h1 className="">MentUp‚Äôa Giri≈ü Yap</h1>
          <form onSubmit={handleSubmit}>
            <div className="input-group">
              <label htmlFor="email">E-posta</label>
              <input
                type="email"
                id="email"
                placeholder="johndoe@example.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="input-group">
              <label htmlFor="password">≈ûifre</label>
              <div className="password-wrapper">
                <input
                  type="password"
                  id="password"
                  placeholder="********"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
                <button type="button" className="toggle-password">üëÅ</button>
              </div>
            </div>
            <div className="options">
              <label className="remember-me">
                <input type="checkbox" /> Beni Hatƒ±rla
              </label>
              <a href="#" className="forgot-password">
                ≈ûifremi unuttum?
              </a>
            </div>
            <button type="submit" className="loginn-button" disabled={isLoading}>
              {isLoading ? "Y√ºkleniyor..." : "Gƒ∞Rƒ∞≈û YAP"}
            </button>
          </form>
          {message && <p className="message">{message}</p>}
        </div>
      </div>
    </div>
  );
};

export default Login;
