import React, { useState } from 'react';
import './SignUp.css';
import { useNavigate } from 'react-router-dom';


const SignUp = () => {
    const [name, setName] = useState("");
    const [password, setPassword] = useState("");
    const [email, setEmail] = useState("");
    const [message, setMessage] = useState("");

    const navigate = useNavigate(); // useNavigate hook'u kullanılıyor.

    const handleLoginClick = () => {
        navigate('/login'); // /login sayfasına yönlendirir.
    };

    async function handleSignUp(e) {
        e.preventDefault(); // Sayfanın yeniden yüklenmesini engeller
        const response = await fetch("http://localhost:5001/auth/signup", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                name: name,
                email: email,
                password: password
            })
        });
        if (!response.ok) {
            console.log("Fetch atarken hata geldi!");
        }
        // Kayıt olduktan sonra yapılacak işlemler buraya eklenir

        const data = await response.json();
        setMessage(data.message || "Kayıt Başarılı!");
    }


  return (
    <div>
      <button className="login-button-signup-page" onClick={handleLoginClick}>Giriş Yap</button>
    <div className="signup-container">
    <div className="logo-name">
          <a href='/home'>MentUp</a>
          </div>
      <div className="signup-box">
        <h2>MentUp&#39;a Kaydol</h2>
        <form>
          <div className="form-group">
            <label htmlFor="name">Ad Soyad</label>
            <input type="text" id="name" value={name} onChange={(e) => setName(e.target.value)} placeholder="John" required />
            </div>
          <div className="form-group">
            <label htmlFor="email">E-Posta</label>
            <input type="email" id="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="johndoe@example.com" required />
            </div>
          <div className="form-group">
            <label htmlFor="password">Şifre</label>
            <div className="password-wrapper">
            <input type="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="********" required />
            <button type="button" className="toggle-password">👁</button>
            </div>
          </div>
          <div className="form-group checkbox">
            <input type="checkbox" id="terms" required />
            <label htmlFor="terms">Hizmet Şartlarını ve Gizlilik Politikasını kabul ediyorum.</label>
          </div>
          <button type="submit" className="submit-button" onClick={handleSignUp}>KAYDOL</button>
        </form>
      </div>
      {/* <button className="login-button">Giriş Yap</button> */}
    </div>
    </div>
  );
};

export default SignUp;

