import { useState, useEffect } from "react";
import "./MenteeProfile.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircleUser } from "@fortawesome/free-solid-svg-icons";
import { faUser } from "@fortawesome/free-solid-svg-icons";
import { faGear } from "@fortawesome/free-solid-svg-icons";
import { faCreditCard } from "@fortawesome/free-solid-svg-icons";
import { faReceipt } from "@fortawesome/free-solid-svg-icons";
import NavBar2 from "../../components/NavBar2/Navbar2";

const MenteeProfile = () => {
  const [profile, setProfile] = useState({
    firstName: "",
    lastName: "",
    displayName: "",
    occupation: "",
    birthPlace: "",
    location: "",
    skills: "",
    languages: "",
    profilePhoto: null
  });

  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await fetch('/api/auth/profile', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        if (!res.ok) throw new Error('Profil alınırken hata oluştu');
        const { user } = await res.json();
        const p = user.profile || {};
        setProfile({
          firstName: p.firstName || '',
          lastName: p.lastName || '',
          displayName: p.displayName || '',
          occupation: p.occupation || '',
          birthPlace: p.birthPlace || '',
          location: p.location || '',
          skills: p.skills ? p.skills.join(', ') : '',
          languages: p.languages ? p.languages.join(', ') : '',
          profilePhoto: p.profilePhoto || null
        });
      } catch (err) {
        console.error(err);
      }
    };
    if (token) fetchProfile();
  }, [token]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile(prev => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setProfile(prev => ({ ...prev, profilePhoto: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSave = async () => {
    try {
      const res = await fetch('/api/auth/profile', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          firstName: profile.firstName,
          lastName: profile.lastName,
          displayName: profile.displayName,
          occupation: profile.occupation,
          birthPlace: profile.birthPlace,
          location: profile.location,
          skills: profile.skills.split(',').map(s => s.trim()),
          languages: profile.languages.split(',').map(l => l.trim()),
          profilePhoto: profile.profilePhoto
        })
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || 'Güncelleme hatası');
      alert('Profil başarıyla güncellendi');
    } catch (err) {
      console.error(err);
      alert('Profil güncellenirken hata oluştu');
    }
  };

  return (
    <div className="mentee-profile-container">
