import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./adminPanel.css";
import axios from "axios";

const AdminPanel = () => {
  const { id } = useParams(); // URL'den başvuru ID'sini al
  const [application, setApplication] = useState(null);

  useEffect(() => {
    const fetchApplication = async () => {
      try {
        const response = await axios.get(`http://localhost:5001/admin/applications/${id}`);
        setApplication(response.data);
      } catch (err) {
        console.error('Başvuru detayları alınamadı:', err);
      }
    };

    fetchApplication();
  }, [id]);

  if (!application) {
    return <p>Yükleniyor...</p>;
  }

  return (
    <div className="admin-panel-container">
      <h1 className="admin-panel-title">Mentorluk Başvurusu Detayları</h1>

      <div className="admin-panel-form-card">
        <div className="admin-panel-form-item">
          <label>İsim:</label>
          <p>{application.name}</p>
        </div>

        <div className="admin-panel-form-item">
          <label>Soyisim:</label>
          <p>{application.surname}</p>
        </div>

        <div className="admin-panel-form-item">
          <label>Yaş:</label>
          <p>{application.age}</p>
        </div>

        <div className="admin-panel-form-item">
          <label>E-posta:</label>
          <p>{application.email}</p>
        </div>

        <div className="admin-panel-form-item">
          <label>Diploma Numarası:</label>
          <p>{application.degree_number}</p>
        </div>

        <div className="admin-panel-form-item">
          <label>Tecrübe Yılları:</label>
          <p>{application.experience_years}</p>
        </div>

        <div className="admin-panel-form-item">
          <label>Neden Mentor Olmak İstiyorsunuz?</label>
          <p>{application.why_mentor}</p>
        </div>

        <div className="admin-panel-form-item">
          <label>CV:</label>
          <a href={application.cv_url} className="admin-panel-cv-link" target="_blank" rel="noopener noreferrer">
            CV'yi Görüntüle
          </a>
        </div>

        <div className="admin-panel-form-item">
          <label>Profil Fotoğrafı:</label>
          <img 
            src={application.photo_url || '/default-profile.png'} // Profil fotoğrafı yoksa varsayılan bir resim göster
            alt={`${application.name} ${application.surname}`} 
            className="admin-panel-profile-photo"
          />
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
