import { useState, useEffect } from "react";
import axios from 'axios';
import "./MenteeProfile.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircleUser } from "@fortawesome/free-solid-svg-icons";
import { faUser } from "@fortawesome/free-solid-svg-icons";
import { faGear } from "@fortawesome/free-solid-svg-icons";
import { faCreditCard } from "@fortawesome/free-solid-svg-icons";
import { faReceipt } from "@fortawesome/free-solid-svg-icons";
import NavBar2 from "../../components/NavBar2/Navbar2";

const MenteeProfile = () => {
  const [profilePhoto, setProfilePhoto] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      const token = localStorage.getItem('token');
      if (!token) return;

      try {
        const res = await axios.get('http://localhost:5001/profile/me', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        setUser({
          name: res.data.name,
          surname: res.data.surname,
          email: res.data.email,
          role: res.data.role,
        });

        setProfile(res.data.profile); // profile null olabilir, kontrol edersin
      } catch (err) {
        console.error("Profil alınırken hata:", err);
      }
    };

    fetchProfile();
  }, []);

  if (!user) return <p>Yükleniyor...</p>


  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setProfilePhoto(reader.result); // Resmin Base64 formatında URL'sini saklar.
      };
      reader.readAsDataURL(file);
    }
  };

  const handleButtonClick = () => {
    document.getElementById("fileInput").click();
  };
  return (
    <div className="mentee-profile-container">
      <header>
        <NavBar2/>
      </header>

      <main>
        <div className="mentee-profile-form">
          <h1 className="mentee-profile-title">Ayarlar</h1>
          <div className="all-settings-form">
            <div className="photo-settings-card">
              <div className="profile-photo-card">
                <div>
                  <div className="profile-photo">
                    {profilePhoto ? (
                      <img
                        src={profilePhoto}
                        alt="Profil"
                        className="profile-photo-preview"
                      />
                    ) : (
                      <FontAwesomeIcon icon={faCircleUser} className="profile-photo-icon" />
                    )}
                  </div>
                  <div className="profile-photo-text">
                    <h3 className="profile-photo-text-info">Profil Resmini Değiştir</h3>
