import { useState, useEffect } from "react";
import "./MenteeProfile.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircleUser } from "@fortawesome/free-solid-svg-icons";
import NavBar2 from "../../components/NavBar2/Navbar2";

const MenteeProfile = () => {
  const [profile, setProfile] = useState({
    firstName: "",
    lastName: "",
    displayName: "",
    occupation: "",
    birthPlace: "",
    location: "",
    skills: "",
    languages: "",
    profilePhoto: null
  });

  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await fetch('/api/auth/profile', {
          headers: { Authorization: `Bearer ${token}` }
        });
        if (!res.ok) throw new Error('Profil alınırken hata oluştu');
        const { user } = await res.json();
        const p = user.profile || {};
        setProfile({
          firstName: p.firstName || '',
          lastName: p.lastName || '',
          displayName: p.displayName || '',
          occupation: p.occupation || '',
          birthPlace: p.birthPlace || '',
          location: p.location || '',
          skills: p.skills ? p.skills.join(', ') : '',
          languages: p.languages ? p.languages.join(', ') : '',
          profilePhoto: p.profilePhoto || null
        });
      } catch (err) {
        console.error(err);
      }
    };
    if (token) fetchProfile();
  }, [token]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile(prev => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setProfile(prev => ({ ...prev, profilePhoto: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleButtonClick = () => {
    document.getElementById('fileInput').click();
  };

  const handleSave = async () => {
    try {
      const res = await fetch('/api/auth/profile', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({
          firstName: profile.firstName,
          lastName: profile.lastName,
          displayName: profile.displayName,
          occupation: profile.occupation,
          birthPlace: profile.birthPlace,
          location: profile.location,
          skills: profile.skills.split(',').map(s => s.trim()),
          languages: profile.languages.split(',').map(l => l.trim()),
          profilePhoto: profile.profilePhoto
        })
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || 'Güncelleme hatası');
      alert('Profil başarıyla güncellendi');
    } catch (err) {
      console.error(err);
      alert('Profil güncellenirken hata oluştu');
    }
  };

  return (
    <div className="mentee-profile-container">
      <header><NavBar2/></header>
      <main>
        <div className="mentee-profile-form">

          {/* Fotoğraf ve gezinme barı */}
          <div className="photo-settings-card">
            <div className="profile-photo-card">
              <div className="profile-photo">
                {profile.profilePhoto ? (
                  <img src={profile.profilePhoto} alt="Profil" className="profile-photo-preview" />
                ) : (
                  <FontAwesomeIcon icon={faCircleUser} className="profile-photo-icon" />
                )}
              </div>
              <button onClick={handleButtonClick} className="profile-photo-button-upload">
                Resim Yükle
              </button>
              <input
                id="fileInput"
                type="file"
                accept=".jpg,.png"
                style={{ display: 'none' }}
                onChange={handleFileChange}
              />
            </div>
          </div>

          {/* Profil formu */}
          <div className="profile-settings-form">
            <h2>Profil Ayarları</h2>
            <div className="profile-settings-infos">
              <div>
                <label>İsim *</label>
                <input
                  name="firstName"
                  value={profile.firstName}
                  onChange={handleChange}
                  placeholder="John"
                />
              </div>
              <div>
                <label>Soyisim *</label>
                <input
                  name="lastName"
                  value={profile.lastName}
                  onChange={handleChange}
                  placeholder="Doe"
                />
              </div>
              <div>
                <label>Görünen İsim *</label>
                <input
                  name="displayName"
                  value={profile.displayName}
                  onChange={handleChange}
                  placeholder="John_Doe"
                />
              </div>
              <div>
                <label>Meslek *</label>
                <input
                  name="occupation"
                  value={profile.occupation}
                  onChange={handleChange}
                />
              </div>
              <div>
                <label>Doğum Yeri *</label>
                <input
                  name="birthPlace"
                  value={profile.birthPlace}
                  onChange={handleChange}
                />
              </div>
              <div>
                <label>Lokasyon *</label>
                <input
                  name="location"
                  value={profile.location}
                  onChange={handleChange}
                />
              </div>
              <div>
                <label>Beceri Alanları</label>
                <input
                  name="skills"
                  value={profile.skills}
                  onChange={handleChange}
                  placeholder=", ile ayırarak giriniz"
                />
              </div>
              <div>
                <label>Bilinen Diller</label>
                <input
                  name="languages"
                  value={profile.languages}
                  onChange={handleChange}
                  placeholder=", ile ayırarak giriniz"
                />
              </div>
              <button onClick={handleSave} className="profile-settings-button-save">
                Kaydet
              </button>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default MenteeProfile;
