import React, { useEffect, useState } from "react";
import axios from "axios";
import "./adminPanel.css";

const AdminPanel = () => {
  const [applications, setApplications] = useState([]);

  useEffect(() => {
    const fetchApplications = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:5001/admin/mentor-applications", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setApplications(response.data);
      } catch (err) {
        console.error("Başvurular alınamadı:", err);
      }
    };

    fetchApplications();
  }, []);

  return (
    <div className="admin-panel-container">
      <h1 className="admin-panel-title">Mentorluk Başvuruları</h1>

      {applications.map((application, index) => (
        <div key={index} className="admin-panel-form-card">
          <div className="admin-panel-form-item">
            <label>İsim:</label>
            <p>{application.User.name}</p>
          </div>

          <div className="admin-panel-form-item">
            <label>Soyisim:</label>
            <p>{application.User.surname}</p>
          </div>

          <div className="admin-panel-form-item">
            <label>E-posta:</label>
            <p>{application.User.email}</p>
          </div>

          <div className="admin-panel-form-item">
            <label>Yaş:</label>
            <p>{application.age}</p>
          </div>

          <div className="admin-panel-form-item">
            <label>Diploma Numarası:</label>
            <p>{application.degree_number}</p>
          </div>

          <div className="admin-panel-form-item">
            <label>Bilgisayar mühendisliği sektöründe kaç yıllık tecrübeniz var?</label>
            <p>{application.experience_years}</p>
          </div>

          <div className="admin-panel-form-item">
            <label>Neden mentor olmak istiyorsunuz?</label>
            <p>{application.why_mentor}</p>
          </div>

          <div className="admin-panel-form-item">
            <label>Özgeçmiş (CV):</label>
            <a href={application.cv_url} target="_blank" rel="noopener noreferrer" className="admin-panel-cv-link">
              CV'yi Görüntüle
            </a>
          </div>

          <div className="admin-panel-action-buttons">
            <button className="admin-button reject">Reddet</button>
            <button className="admin-button approve">Onayla</button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default AdminPanel;
