import { useState, useEffect } from "react";
import axios from 'axios';
import "./MenteeProfile.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircleUser, faUser, faGear, faCreditCard, faReceipt } from "@fortawesome/free-solid-svg-icons";
import NavBar2 from "../../components/NavBar2/Navbar2";

const MenteeProfile = () => {
  const [profile, setProfile] = useState({
    firstName: "",
    lastName: "",
    occupation: "",
    birthPlace: "",
    location: "",
    skills: "",
    languages: "",
    profilePhoto: null
  });
  const [tokenChecked, setTokenChecked] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      window.location.href = "/login";
      return;
    }
    setTokenChecked(true);

    const fetchProfile = async () => {
      try {
        const res = await axios.get('/api/auth/profile', {
          headers: { Authorization: `Bearer ${token}` }
        });
        const { user } = res.data;
        const p = user.profile || {};
        setProfile({
          firstName: p.firstName || '',
          lastName: p.lastName || '',
          occupation: p.occupation || '',
          birthPlace: p.birthPlace || '',
          location: p.location || '',
          skills: Array.isArray(p.skills) ? p.skills.join(', ') : '',
          languages: Array.isArray(p.languages) ? p.languages.join(', ') : '',
          profilePhoto: p.profilePhoto || null
        });
      } catch (err) {
        console.error('Profil alınırken hata:', err);
      }
    };
    fetchProfile();
  }, []);

  if (!tokenChecked) return <p>Yükleniyor...</p>;

  const handleChange = e => {
    const { name, value } = e.target;
    setProfile(prev => ({ ...prev, [name]: value }));
  };

  const handleFileChange = event => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setProfile(prev => ({ ...prev, profilePhoto: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleButtonClick = () => {
    document.getElementById("fileInput").click();
  };

  const handleSave = async () => {
    const token = localStorage.getItem('token');
    try {
      await axios.put('/api/auth/profile', {
        firstName: profile.firstName,
        lastName: profile.lastName,
        occupation: profile.occupation,
        birthPlace: profile.birthPlace,
        location: profile.location,
        skills: profile.skills.split(',').map(s => s.trim()),
        languages: profile.languages.split(',').map(l => l.trim()),
        profilePhoto: profile.profilePhoto
      }, {
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        }
      });
      alert('Profil başarıyla güncellendi');
    } catch (err) {
      console.error('Profil güncellenirken hata:', err);
      alert('Profil güncellenirken hata oluştu');
    }
  };

  return (
    <div className="mentee-profile-container">
      <header>
        <NavBar2 />
      </header>
      <main>
        <div className="mentee-profile-form">
          <h1 className="mentee-profile-title">Ayarlar</h1>
          <div className="all-settings-form">
            <div className="photo-settings-card">
              <div className="profile-photo-card">
                <div className="profile-photo">
                  {profile.profilePhoto ? (
                    <img src={profile.profilePhoto} alt="Profil" className="profile-photo-preview" />
                  ) : (
                    <FontAwesomeIcon icon={faCircleUser} className="profile-photo-icon" />
                  )}
                </div>
                <button className="profile-photo-button-upload" onClick={handleButtonClick}>
                  Resim Yükle
                </button>
                <input
                  id="fileInput"
                  type="file"
                  accept=".jpg,.png"
                  style={{ display: 'none' }}
                  onChange={handleFileChange}
                />
              </div>
              <div className="profile-settings-bar">
                <a href="/menteeprofile" className="profile-settings-bar-profile-option">
                  <FontAwesomeIcon icon={faUser} /> Profil Bilgileri
                </a>
                <a href="/accountsettings" className="profile-settings-bar-account-settings-option">
                  <FontAwesomeIcon icon={faGear} /> Hesap Bilgileri
                </a>
                <a href="#" className="profile-settings-bar-subscription-option">
                  <FontAwesomeIcon icon={faCreditCard} /> Abonelik
                </a>
                <a href="#" className="profile-settings-bar-payment-history-option">
                  <FontAwesomeIcon icon={faReceipt} /> Ödeme Geçmişi
                </a>
              </div>
            </div>
            <div className="profile-settings-form">
              <h2 className="profile-settings-form-title">Profil Ayarları</h2>
              <h6 className="required-info-text">* Zorunlu alanlar</h6>
              <div className="profile-settings-infos">
                <div>
                  <label>İsim *</label>
                  <input
                    name="firstName"
                    value={profile.firstName}
                    onChange={handleChange}
                    placeholder="John"
                  />
                </div>
                <div>
                  <label>Soyisim *</label>
                  <input
                    name="lastName"
                    value={profile.lastName}
                    onChange={handleChange}
                    placeholder="Doe"
                  />
                </div>
                <div>
                  <label>Meslek *</label>
                  <input
                    name="occupation"
                    value={profile.occupation}
                    onChange={handleChange}
                  />
                </div>
                <div>
                  <label>Doğum Yeri *</label>
                  <select
                    name="birthPlace"
                    value={profile.birthPlace}
                    onChange={handleChange}
                  >
                    <option value="" disabled>Ülke Seçin</option>
                    <option value="Türkiye">Türkiye</option>
                    <option value="Almanya">Almanya</option>
                  </select>
                </div>
                <div>
                  <label>Lokasyon *</label>
                  <select
                    name="location"
                    value={profile.location}
                    onChange={handleChange}
                  >
                    <option value="" disabled>Ülke Seçin</option>
                    <option value="Türkiye">Türkiye</option>
                    <option value="Almanya">Almanya</option>
                  </select>
                </div>
                <div>
                  <label>Beceri Alanları</label>
                  <input
                    name="skills"
                    value={profile.skills}
                    onChange={handleChange}
                    placeholder=", ile ayırarak giriniz"
                  />
                </div>
                <div>
                  <label>Bilinen Diller</label>
                  <input
                    name="languages"
                    value={profile.languages}
                    onChange={handleChange}
                    placeholder=", ile ayırarak giriniz"
                  />
                </div>
                <button className="profile-settings-button-save" onClick={handleSave}>
                  Kaydet
                </button>
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default MenteeProfile;
